pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

struct ServiceParams {
    string serviceType;
    string url;
    string model;
    string verifiability;
    uint inputPrice;
    uint outputPrice;
    string additionalInfo;
    address teeSignerAddress;
}

struct Service {
    address provider;
    string serviceType;
    string url;
    uint inputPrice;
    uint outputPrice;
    uint updatedAt;
    string model;
    string verifiability;
    string additionalInfo;
    address teeSignerAddress;
    bool teeSignerAcknowledged;
}

library ServiceLibrary {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    error ServiceNotExist(address provider);

    struct ServiceMap {
        EnumerableSet.Bytes32Set _keys;
        mapping(bytes32 => Service) _values;
    }

    function getService(ServiceMap storage map, address provider) internal view returns (Service storage) {
        return _get(map, provider);
    }

    function getAllServices(ServiceMap storage map) internal view returns (Service[] memory services) {
        uint len = _length(map);
        services = new Service[](len);
        for (uint i = 0; i < len; ++i) {
            services[i] = _at(map, i);
        }
    }

    function addOrUpdateService(ServiceMap storage map, address provider, ServiceParams calldata params) internal {
        bytes32 key = _key(provider);
        if (!_contains(map, key)) {
            _set(
                map,
                key,
                Service(
                    provider,
                    params.serviceType,
                    params.url,
                    params.inputPrice,
                    params.outputPrice,
                    block.timestamp,
                    params.model,
                    params.verifiability,
                    params.additionalInfo,
                    params.teeSignerAddress,
                    false  // teeSignerAcknowledged - default to false, needs owner acknowledgement
                )
            );
            return;
        }
        Service storage value = _get(map, provider);

        // Check if critical fields are being changed (fields that require re-acknowledgement)
        bool criticalFieldsChanged = (
            keccak256(bytes(value.serviceType)) != keccak256(bytes(params.serviceType)) ||
            keccak256(bytes(value.model)) != keccak256(bytes(params.model)) ||
            keccak256(bytes(value.verifiability)) != keccak256(bytes(params.verifiability)) ||
            value.teeSignerAddress != params.teeSignerAddress ||
            keccak256(bytes(value.additionalInfo)) != keccak256(bytes(params.additionalInfo))
        );

        // Update all fields
        value.serviceType = params.serviceType;
        value.inputPrice = params.inputPrice;
        value.outputPrice = params.outputPrice;
        value.url = params.url;
        value.updatedAt = block.timestamp;
        value.model = params.model;
        value.verifiability = params.verifiability;
        value.additionalInfo = params.additionalInfo;
        value.teeSignerAddress = params.teeSignerAddress;

        // Reset acknowledgement if critical fields changed
        // Only price and URL changes don't require re-acknowledgement
        if (criticalFieldsChanged) {
            value.teeSignerAcknowledged = false;
        }
    }

    function removeService(ServiceMap storage map, address provider) internal {
        bytes32 key = _key(provider);
        if (!_contains(map, key)) {
            revert ServiceNotExist(provider);
        }
        _remove(map, key);
    }

    function acknowledgeTEESigner(ServiceMap storage map, address provider) internal {
        Service storage service = _get(map, provider);
        service.teeSignerAcknowledged = true;
    }

    function revokeTEESignerAcknowledgement(ServiceMap storage map, address provider) internal {
        Service storage service = _get(map, provider);
        service.teeSignerAcknowledged = false;
    }

    function _at(ServiceMap storage map, uint index) internal view returns (Service storage) {
        bytes32 key = map._keys.at(index);
        return map._values[key];
    }

    function _set(ServiceMap storage map, bytes32 key, Service memory value) internal returns (bool) {
        map._values[key] = value;
        return map._keys.add(key);
    }

    function _get(ServiceMap storage map, address provider) internal view returns (Service storage) {
        bytes32 key = _key(provider);
        Service storage value = map._values[key];
        if (!_contains(map, key)) {
            revert ServiceNotExist(provider);
        }
        return value;
    }

    function _remove(ServiceMap storage map, bytes32 key) internal returns (bool) {
        delete map._values[key];
        return map._keys.remove(key);
    }

    function _contains(ServiceMap storage map, bytes32 key) internal view returns (bool) {
        return map._keys.contains(key);
    }

    function _length(ServiceMap storage map) internal view returns (uint) {
        return map._keys.length();
    }

    function _key(address provider) internal pure returns (bytes32) {
        return keccak256(abi.encode(provider));
    }
}
","keccak256":"0x4ad3c854c1c477776cfa4323182d24d7656297f3a31e08af8a6a816aa9687952","license":"Unlicense"},"contracts/inference/InferenceServing.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "../utils/Initializable.sol";
import "./InferenceAccount.sol";
import "./InferenceService.sol";
import "../ledger/LedgerManager.sol";

struct TEESettlementData {
    address user;
    address provider;
    uint totalFee;
    bytes32 requestsHash;
    uint nonce;
    bytes signature;
}

enum SettlementStatus {
    SUCCESS,              // 0: Full settlement success
    PARTIAL,              // 1: Partial settlement (insufficient balance)
    PROVIDER_MISMATCH,    // 2: Provider mismatch
    NO_TEE_SIGNER,        // 3: TEE signer not acknowledged
    INVALID_NONCE,        // 4: Invalid or duplicate nonce
    INVALID_SIGNATURE     // 5: Signature verification failed
}

contract InferenceServing is Ownable, Initializable, ReentrancyGuard, IServing, ERC165 {
    using AccountLibrary for AccountLibrary.AccountMap;
    using ServiceLibrary for ServiceLibrary.ServiceMap;

    // @custom:storage-location erc7201:0g.serving.inference.v1.0
    struct InferenceServingStorage {
        uint lockTime;
        address ledgerAddress;
        ILedger ledger;
        AccountLibrary.AccountMap accountMap;
        ServiceLibrary.ServiceMap serviceMap;
    }

    // keccak256(abi.encode(uint256(keccak256("0g.serving.inference.v1.0")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 private constant INFERENCE_SERVING_STORAGE_LOCATION = 0xdfd123095cdedb1cecbc229b30f7cf8745fb3d3951645ac4a8fa4c0895f89500;

    function _getInferenceServingStorage() private pure returns (InferenceServingStorage storage $) {
        assembly {
            $.slot := INFERENCE_SERVING_STORAGE_LOCATION
        }
    }

    // Public getters for compatibility
    function lockTime() public view returns (uint) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.lockTime;
    }

    function ledgerAddress() public view returns (address) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.ledgerAddress;
    }

    event BalanceUpdated(address indexed user, address indexed provider, uint amount, uint pendingRefund);
    event RefundRequested(address indexed user, address indexed provider, uint indexed index, uint timestamp);
    event ServiceUpdated(
        address indexed service,
        string serviceType,
        string url,
        uint inputPrice,
        uint outputPrice,
        uint updatedAt,
        string model,
        string verifiability
    );
    event ServiceRemoved(address indexed service);
    event TEESettlementResult(address indexed user, SettlementStatus status, uint256 unsettledAmount);
    event BatchBalanceUpdated(address[] users, uint256[] balances, uint256[] pendingRefunds);
    event ProviderTEESignerAcknowledged(address indexed provider, address indexed teeSignerAddress, bool acknowledged);
    error InvalidTEESignature(string reason);


    function initialize(uint _locktime, address _ledgerAddress, address owner) public onlyInitializeOnce {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        _transferOwnership(owner);
        $.lockTime = _locktime;
        $.ledgerAddress = _ledgerAddress;
        $.ledger = ILedger(_ledgerAddress);
    }

    modifier onlyLedger() {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        require(msg.sender == $.ledgerAddress, "Caller is not the ledger contract");
        _;
    }

    function updateLockTime(uint _locktime) public onlyOwner {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.lockTime = _locktime;
    }

    function getAccount(address user, address provider) public view returns (Account memory) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.accountMap.getAccount(user, provider);
    }

    function getAllAccounts(uint offset, uint limit) public view returns (Account[] memory accounts, uint total) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        require(limit == 0 || limit <= 50, "Limit too large");
        return $.accountMap.getAllAccounts(offset, limit);
    }

    function getAccountsByProvider(
        address provider,
        uint offset,
        uint limit
    ) public view returns (Account[] memory accounts, uint total) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        require(limit == 0 || limit <= 50, "Limit too large");
        return $.accountMap.getAccountsByProvider(provider, offset, limit);
    }

    function getAccountsByUser(
        address user,
        uint offset,
        uint limit
    ) public view returns (Account[] memory accounts, uint total) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        require(limit == 0 || limit <= 50, "Limit too large");
        return $.accountMap.getAccountsByUser(user, offset, limit);
    }

    function getBatchAccountsByUsers(address[] calldata users) external view returns (Account[] memory accounts) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.accountMap.getBatchAccountsByUsers(users, msg.sender);
    }

    function acknowledgeTEESignerByOwner(address provider) external onlyOwner {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        Service storage service = $.serviceMap.getService(provider);
        $.serviceMap.acknowledgeTEESigner(provider);
        emit ProviderTEESignerAcknowledged(provider, service.teeSignerAddress, true);
    }

    function acknowledgeTEESigner(address provider, bool acknowledged) external {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.accountMap.acknowledgeTEESigner(msg.sender, provider, acknowledged);
    }


    function revokeTEESignerAcknowledgement(address provider) external onlyOwner {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        Service storage service = $.serviceMap.getService(provider);
        $.serviceMap.revokeTEESignerAcknowledgement(provider);
        emit ProviderTEESignerAcknowledged(provider, service.teeSignerAddress, false);
    }

    function accountExists(address user, address provider) public view returns (bool) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.accountMap.accountExists(user, provider);
    }

    function getPendingRefund(address user, address provider) public view returns (uint) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        return $.accountMap.getPendingRefund(user, provider);
    }

    function addAccount(
        address user,
        address provider,
        string memory additionalInfo
    ) external payable onlyLedger {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        (uint balance, uint pendingRefund) = $.accountMap.addAccount(user, provider, msg.value, additionalInfo);
        emit BalanceUpdated(user, provider, balance, pendingRefund);
    }

    function deleteAccount(address user, address provider) external onlyLedger {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.accountMap.deleteAccount(user, provider);
    }

    function depositFund(address user, address provider, uint cancelRetrievingAmount) external payable onlyLedger {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        (uint balance, uint pendingRefund) = $.accountMap.depositFund(user, provider, cancelRetrievingAmount, msg.value);
        emit BalanceUpdated(user, provider, balance, pendingRefund);
    }

    function requestRefundAll(address user, address provider) external onlyLedger {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.accountMap.requestRefundAll(user, provider);
        Account memory account = $.accountMap.getAccount(user, provider);
        if (account.refunds.length > 0) {
            emit RefundRequested(user, provider, account.refunds.length - 1, block.timestamp);
        }
    }

    function processRefund(
        address user,
        address provider
    ) external onlyLedger returns (uint totalAmount, uint balance, uint pendingRefund) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        (totalAmount, balance, pendingRefund) = $.accountMap.processRefund(user, provider, $.lockTime);

        if (totalAmount > 0) {
            (bool success, ) = payable(msg.sender).call{value: totalAmount}("");
            require(success, "transfer to ledger failed");
            emit BalanceUpdated(user, provider, balance, pendingRefund);
        }
    }

    function getService(address provider) public view returns (Service memory service) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        service = $.serviceMap.getService(provider);
    }

    function getAllServices() public view returns (Service[] memory services) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        services = $.serviceMap.getAllServices();
    }

    function addOrUpdateService(ServiceParams calldata params) external {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.serviceMap.addOrUpdateService(msg.sender, params);
        emit ServiceUpdated(
            msg.sender,
            params.serviceType,
            params.url,
            params.inputPrice,
            params.outputPrice,
            block.timestamp,
            params.model,
            params.verifiability
        );
    }

    function removeService() external {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        $.serviceMap.removeService(msg.sender);
        emit ServiceRemoved(msg.sender);
    }

    function _settleFees(Account storage account, uint amount) private {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        if (amount > (account.balance - account.pendingRefund)) {
            uint remainingFee = amount - (account.balance - account.pendingRefund);
            account.pendingRefund -= remainingFee;
            for (int i = int(account.refunds.length - 1); i >= 0; i--) {
                Refund storage refund = account.refunds[uint(i)];
                if (refund.processed) {
                    continue;
                }

                if (refund.amount <= remainingFee) {
                    remainingFee -= refund.amount;
                } else {
                    refund.amount -= remainingFee;
                    remainingFee = 0;
                }

                if (remainingFee == 0) {
                    break;
                }
            }
        }
        account.balance -= amount;
        $.ledger.spendFund(account.user, amount);
        emit BalanceUpdated(account.user, msg.sender, account.balance, account.pendingRefund);
    }

    // Static view function for previewing settlement results without state changes
    function previewSettlementResults(
        TEESettlementData[] calldata settlements
    ) external view returns (
        address[] memory failedUsers,
        SettlementStatus[] memory failureReasons,
        address[] memory partialUsers, 
        uint256[] memory partialAmounts
    ) {
        require(settlements.length > 0, "No settlements provided");

        failedUsers = new address[](settlements.length);
        failureReasons = new SettlementStatus[](settlements.length);
        partialUsers = new address[](settlements.length);
        partialAmounts = new uint256[](settlements.length);

        uint failedCount = 0;
        uint partialCount = 0;

        for (uint i = 0; i < settlements.length; i++) {
            TEESettlementData calldata settlement = settlements[i];

            if (settlement.provider != msg.sender) {
                _recordFailure(failedUsers, failureReasons, failedCount++, settlement.user, SettlementStatus.PROVIDER_MISMATCH);
                continue;
            }

            (SettlementStatus status, uint256 unsettledAmount) = _previewTEESettlement(settlement);

            if (status == SettlementStatus.SUCCESS) {
                continue;
            }

            if (status == SettlementStatus.PARTIAL) {
                _recordPartial(partialUsers, partialAmounts, partialCount++, settlement.user, unsettledAmount);
                continue;
            }
            _recordFailure(failedUsers, failureReasons, failedCount++, settlement.user, status);
        }

        assembly {
            mstore(failedUsers, failedCount)
            mstore(failureReasons, failedCount)
            mstore(partialUsers, partialCount)
            mstore(partialAmounts, partialCount)
        }
    }

    function settleFeesWithTEE(
        TEESettlementData[] calldata settlements
    ) external returns (
        address[] memory failedUsers,
        SettlementStatus[] memory failureReasons,
        address[] memory partialUsers, 
        uint256[] memory partialAmounts
    ) {
        require(settlements.length > 0, "No settlements provided");
        require(settlements.length <= 50, "Too many settlements in batch");

        failedUsers = new address[](settlements.length);
        failureReasons = new SettlementStatus[](settlements.length);
        partialUsers = new address[](settlements.length);
        partialAmounts = new uint256[](settlements.length);

        uint failedCount = 0;
        uint partialCount = 0;
        uint256 totalTransferAmount = 0;

        for (uint i = 0; i < settlements.length; i++) {
            TEESettlementData calldata settlement = settlements[i];

            if (settlement.provider != msg.sender) {
                _recordFailure(failedUsers, failureReasons, failedCount++, settlement.user, SettlementStatus.PROVIDER_MISMATCH);
                emit TEESettlementResult(settlement.user, SettlementStatus.PROVIDER_MISMATCH, settlement.totalFee);
                continue;
            }

            (SettlementStatus status, uint256 unsettledAmount, uint256 settledAmount) = _processTEESettlement(settlement);
            totalTransferAmount += settledAmount;
            emit TEESettlementResult(settlement.user, status, unsettledAmount);

            if (status == SettlementStatus.SUCCESS) {
                continue;
            }

            if (status == SettlementStatus.PARTIAL) {
                _recordPartial(partialUsers, partialAmounts, partialCount++, settlement.user, unsettledAmount);
                continue;
            }
            _recordFailure(failedUsers, failureReasons, failedCount++, settlement.user, status);
        }

        assembly {
            mstore(failedUsers, failedCount)
            mstore(failureReasons, failedCount)
            mstore(partialUsers, partialCount)
            mstore(partialAmounts, partialCount)
        }

        // Batch transfer all settled amounts at once
        if (totalTransferAmount > 0) {
            payable(msg.sender).transfer(totalTransferAmount);
        }
    }

    // View function to preview settlement without state changes
    function _previewTEESettlement(TEESettlementData calldata settlement) private view returns (SettlementStatus status, uint256 unsettledAmount) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        Account storage account = $.accountMap.getAccount(settlement.user, msg.sender);

        // Validate TEE signer acknowledgement - both user and service must be acknowledged, and service must have valid TEE signer address
        Service storage service = $.serviceMap.getService(msg.sender);
        if (!account.acknowledged || !service.teeSignerAcknowledged || service.teeSignerAddress == address(0)) {
            return (SettlementStatus.NO_TEE_SIGNER, settlement.totalFee);
        }

        // Validate nonce (check if nonce would be valid)
        if (account.nonce >= settlement.nonce) {
            return (SettlementStatus.INVALID_NONCE, settlement.totalFee);
        }

        // Validate signature using service TEE signer address
        if (!_verifySignature(settlement, service.teeSignerAddress)) {
            return (SettlementStatus.INVALID_SIGNATURE, settlement.totalFee);
        }

        // Calculate settlement amounts (without modifying state)
        uint256 balance = account.balance;
        uint256 unsettled = settlement.totalFee > balance ? settlement.totalFee - balance : 0;

        // Return appropriate status
        if (unsettled > 0) {
            return (SettlementStatus.PARTIAL, unsettled);
        } else {
            return (SettlementStatus.SUCCESS, 0);
        }
    }

    function _processTEESettlement(TEESettlementData calldata settlement) private returns (SettlementStatus status, uint256 unsettledAmount, uint256 settledAmount) {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        Account storage account = $.accountMap.getAccount(settlement.user, msg.sender);

        // Validate TEE signer acknowledgement - both user and service must be acknowledged, and service must have valid TEE signer address
        Service storage service = $.serviceMap.getService(msg.sender);
        if (!account.acknowledged || !service.teeSignerAcknowledged || service.teeSignerAddress == address(0)) {
            return (SettlementStatus.NO_TEE_SIGNER, settlement.totalFee, 0);
        }

        // Validate nonce
        if (account.nonce >= settlement.nonce) {
            return (SettlementStatus.INVALID_NONCE, settlement.totalFee, 0);
        }

        // Validate signature using service TEE signer address
        if (!_verifySignature(settlement, service.teeSignerAddress)) {
            return (SettlementStatus.INVALID_SIGNATURE, settlement.totalFee, 0);
        }

        // All validations passed, update nonce
        account.nonce = settlement.nonce;

        // Calculate settlement amounts
        uint256 balance = account.balance;
        uint256 toSettle = settlement.totalFee > balance ? balance : settlement.totalFee;
        uint256 unsettled = settlement.totalFee > balance ? settlement.totalFee - balance : 0;

        // Settle what we can
        if (toSettle > 0) {
            _settleFees(account, toSettle);
        }

        // Return appropriate status
        if (unsettled > 0) {
            return (SettlementStatus.PARTIAL, unsettled, toSettle);
        } else {
            return (SettlementStatus.SUCCESS, 0, toSettle);
        }
    }

    function _verifySignature(TEESettlementData calldata settlement, address expectedSigner) private pure returns (bool) {
        bytes calldata signature = settlement.signature;
        if (signature.length != 65) return false;

        bytes32 messageHash = keccak256(abi.encodePacked(
            settlement.requestsHash,
            settlement.nonce,
            settlement.provider,
            settlement.user,
            settlement.totalFee
        ));

        bytes32 ethSignedHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash));

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := calldataload(add(signature.offset, 0))
            s := calldataload(add(signature.offset, 32))
            v := byte(0, calldataload(add(signature.offset, 64)))
        }

        if (v < 27) v += 27;

        return ecrecover(ethSignedHash, v, r, s) == expectedSigner;
    }

    function _recordFailure(
        address[] memory failedUsers,
        SettlementStatus[] memory failureReasons,
        uint index,
        address user,
        SettlementStatus reason
    ) private pure {
        failedUsers[index] = user;
        failureReasons[index] = reason;
    }

    function _recordPartial(
        address[] memory partialUsers,
        uint256[] memory partialAmounts,
        uint index,
        address user,
        uint256 amount
    ) private pure {
        partialUsers[index] = user;
        partialAmounts[index] = amount;
    }

    // === ERC165 Support ===

    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IServing).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    receive() external payable {
        InferenceServingStorage storage $ = _getInferenceServingStorage();
        // Use ILedger interface to deposit funds for the sender
        $.ledger.depositFundFor{value: msg.value}(msg.sender);
    }
}
","keccak256":"0x18101fc46d6ca19b912684d6d2994ec592b3d036c634437b527db94b9f182248","license":"MIT"},"contracts/ledger/LedgerManager.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import "../utils/Initializable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
interface IServing {
    function accountExists(address user, address provider) external view returns (bool);
    function getPendingRefund(address user, address provider) external view returns (uint);
    function addAccount(address user, address provider, string memory additionalInfo) external payable;
    function depositFund(address user, address provider, uint cancelRetrievingAmount) external payable;
    function requestRefundAll(address user, address provider) external;
    function processRefund(address user, address provider) external returns (uint totalAmount, uint balance, uint pendingRefund);
    function deleteAccount(address user, address provider) external;
}

// Simplified ledger structure
struct Ledger {
    address user;
    uint availableBalance;
    uint totalBalance;
    string additionalInfo;
}

// Service information structure
struct ServiceInfo {
    address serviceAddress;
    IServing serviceContract;
    string serviceType; // "inference" or "fine-tuning"
    string version;     // "v1.0", "v2.0" etc.
    string fullName;    // "inference-v2.0"
    string description;
    bool isRecommended; // Whether this is the recommended version for this service type
    uint256 registeredAt;
}

interface ILedger {
    function spendFund(address user, uint amount) external;
    function depositFundFor(address recipient) external payable;
}


contract LedgerManager is Ownable, Initializable, ReentrancyGuard {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.AddressSet;
    using ERC165Checker for address;

    // @custom:storage-location erc7201:0g.serving.ledger
    struct LedgerManagerStorage {
        // Service registry (using address as key)
        mapping(address => ServiceInfo) registeredServices;
        mapping(string => address) serviceNameToAddress; // "inference-v2.0" => address
        EnumerableSet.AddressSet serviceAddresses;

        LedgerMap ledgerMap;
        mapping(address => mapping(string => EnumerableSet.AddressSet)) userServiceProviders; // user => serviceType => providers
    }

    // keccak256(abi.encode(uint256(keccak256("0g.serving.ledger")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 private constant LEDGER_MANAGER_STORAGE_LOCATION = 0x0bb5d42557ea6926c17416c5b1c1c29c28d9006d6f713295a2d385f07156ed00;

    function _getLedgerManagerStorage() private pure returns (LedgerManagerStorage storage $) {
        assembly {
            $.slot := LEDGER_MANAGER_STORAGE_LOCATION
        }
    }

    // Constants
    uint public constant MAX_PROVIDERS_PER_BATCH = 20;
    bytes4 private constant SERVING_INTERFACE_ID = type(IServing).interfaceId;

    // Events
    event ServiceRegistered(address indexed serviceAddress, string serviceName);
    event RecommendedServiceUpdated(string indexed serviceType, string version, address serviceAddress);

    // Errors
    error LedgerNotExists(address user);
    error LedgerExists(address user);
    error InsufficientBalance(address user);
    error TooManyProviders(uint requested, uint maximum);
    error InvalidServiceType(string serviceType);
    error ServiceNotRegistered(address serviceAddress);
    error ServiceNameExists(string serviceName);
    error InvalidServiceAddress(address serviceAddress);

    struct LedgerMap {
        EnumerableSet.Bytes32Set _keys;
        mapping(bytes32 => Ledger) _values;
        // Operation locks to prevent reentrancy
        mapping(bytes32 => bool) _operationLocks;
    }

    // Prevent reentrancy on ledger operations
    modifier withLedgerLock(address user) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = _key(user);
        require(!$.ledgerMap._operationLocks[key], "Ledger locked for operation");
        $.ledgerMap._operationLocks[key] = true;
        _;
        $.ledgerMap._operationLocks[key] = false;
    }

    function initialize(
        address owner
    ) public onlyInitializeOnce {
        _transferOwnership(owner);
    }

    modifier onlyServing() {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        require(
            $.registeredServices[msg.sender].serviceAddress != address(0),
            "Caller is not a registered service"
        );
        _;
    }

    function getLedger(address user) public view returns (Ledger memory) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        return _get($, user);
    }

    function getAllLedgers(uint offset, uint limit) public view returns (Ledger[] memory ledgers, uint total) {
        total = _length();

        if (offset >= total) {
            return (new Ledger[](0), total);
        }

        uint end = limit == 0 ? total : Math.min(offset + limit, total);
        uint resultLen = end - offset;
        ledgers = new Ledger[](resultLen);

        for (uint i = 0; i < resultLen; i++) {
            ledgers[i] = _at(offset + i);
        }

        return (ledgers, total);
    }

    // Get providers for a specific user and service
    function getLedgerProviders(address user, string memory serviceName) public view returns (address[] memory) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        address serviceAddress = $.serviceNameToAddress[serviceName];
        require(serviceAddress != address(0), "Service not found");

        string memory serviceType = $.registeredServices[serviceAddress].serviceType;
        EnumerableSet.AddressSet storage providers = $.userServiceProviders[user][serviceType];
        address[] memory providerList = new address[](providers.length());

        for (uint256 i = 0; i < providers.length(); i++) {
            providerList[i] = providers.at(i);
        }

        return providerList;
    }

    function addLedger(
        string memory additionalInfo
    ) external payable withLedgerLock(msg.sender) returns (uint, uint) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = _key(msg.sender);
        if (_contains($, key)) {
            revert LedgerExists(msg.sender);
        }
        _set($, key, msg.sender, msg.value, additionalInfo);
        return (msg.value, 0);
    }

    function depositFund() external payable withLedgerLock(msg.sender) {
        _depositFundInternal(msg.sender, msg.value);
    }

    // Internal function for deposit logic without modifier
    function _depositFundInternal(address user, uint256 amount) internal {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = _key(user);

        // Create account if it doesn't exist
        if (!_contains($, key)) {
            _set($, key, user, amount, "");
        } else {
            Ledger storage ledger = $.ledgerMap._values[key];
            ledger.availableBalance += amount;
            ledger.totalBalance += amount;
        }
    }

    function depositFundFor(address recipient) external payable withLedgerLock(recipient) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = _key(recipient);

        // Create account if it doesn't exist
        if (!_contains($, key)) {
            _set($, key, recipient, msg.value, "");
        } else {
            Ledger storage ledger = $.ledgerMap._values[key];
            ledger.availableBalance += msg.value;
            ledger.totalBalance += msg.value;
        }
    }

    function refund(uint amount) external withLedgerLock(msg.sender) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        Ledger storage ledger = _get($, msg.sender);
        if (ledger.availableBalance < amount) {
            revert InsufficientBalance(msg.sender);
        }

        ledger.availableBalance -= amount;
        ledger.totalBalance -= amount;
        payable(msg.sender).transfer(amount);
    }

    // Enhanced transferFund with dynamic service support
    function transferFund(
        address provider,
        string memory serviceName,
        uint amount
    ) external withLedgerLock(msg.sender) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        Ledger storage ledger = _get($, msg.sender);

        // Dynamic service lookup
        address serviceAddress = $.serviceNameToAddress[serviceName];
        require(serviceAddress != address(0), "Service not found");

        ServiceInfo storage service = $.registeredServices[serviceAddress];

        address servingAddress = service.serviceAddress;
        IServing serving = service.serviceContract;

        uint transferAmount = amount;
        bytes memory payload;

        if (serving.accountExists(msg.sender, provider)) {
            // Account exists - handle pending refunds
            uint retrievingAmount = serving.getPendingRefund(msg.sender, provider);
            uint cancelRetrievingAmount = Math.min(amount, retrievingAmount);
            transferAmount -= cancelRetrievingAmount;

            payload = abi.encodeWithSignature(
                "depositFund(address,address,uint256)",
                msg.sender,
                provider,
                cancelRetrievingAmount
            );
        } else {
            // New account - use unified addAccount interface
            payload = abi.encodeWithSignature(
                "addAccount(address,address,string)",
                msg.sender,
                provider,
                ledger.additionalInfo
            );

            // Add provider to service storage
            _addProviderToService($, msg.sender, service.serviceType, provider);
        }

        require(ledger.availableBalance >= transferAmount, "Insufficient balance");
        ledger.availableBalance -= transferAmount;

        (bool success, ) = servingAddress.call{value: transferAmount}(payload);
        require(success, "Call to child contract failed");
    }

    function retrieveFund(
        address[] memory providers,
        string memory serviceType
    ) external withLedgerLock(msg.sender) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        if (providers.length > MAX_PROVIDERS_PER_BATCH) {
            revert TooManyProviders(providers.length, MAX_PROVIDERS_PER_BATCH);
        }

        // Dynamic service lookup
        address serviceAddress = $.serviceNameToAddress[serviceType];
        require(serviceAddress != address(0), "Service not found");

        ServiceInfo storage service = $.registeredServices[serviceAddress];

        IServing serving = service.serviceContract;

        Ledger storage ledger = _get($, msg.sender);
        uint totalAmount = 0;

        for (uint i = 0; i < providers.length; i++) {
            (uint amount, , ) = serving.processRefund(msg.sender, providers[i]);
            totalAmount += amount;
            serving.requestRefundAll(msg.sender, providers[i]);
        }
        ledger.availableBalance += totalAmount;
    }

    function deleteLedger() external nonReentrant withLedgerLock(msg.sender) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        Ledger storage ledger = _get($, msg.sender);

        // Safety check: ensure all funds have been withdrawn
        require(ledger.totalBalance == 0, "Must withdraw all funds first");

        bytes32 key = _key(msg.sender);

        // Delete all service accounts dynamically
        uint256 serviceCount = $.serviceAddresses.length();
        for (uint256 i = 0; i < serviceCount; i++) {
            address serviceAddress = $.serviceAddresses.at(i);
            ServiceInfo storage service = $.registeredServices[serviceAddress];

            EnumerableSet.AddressSet storage providers = $.userServiceProviders[msg.sender][service.serviceType];
            address[] memory providerList = new address[](providers.length());
            for (uint j = 0; j < providers.length(); j++) {
                providerList[j] = providers.at(j);
            }

            for (uint j = 0; j < providerList.length; j++) {
                try service.serviceContract.deleteAccount(msg.sender, providerList[j]) {
                    providers.remove(providerList[j]);
                } catch {
                    providers.remove(providerList[j]); // Remove even on failure
                }
            }
        }

        // Delete main ledger
        $.ledgerMap._keys.remove(key);
        delete $.ledgerMap._values[key];
    }

    // === Service Registration Management ===

    function registerService(
        string memory serviceType,
        string memory version,
        address serviceAddress,
        string memory description
    ) external onlyOwner {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        require(serviceAddress != address(0), "Invalid service address");
        require(bytes(serviceType).length > 0, "Service type required");
        require(bytes(version).length > 0, "Version required");
        require($.registeredServices[serviceAddress].serviceAddress == address(0), "Service already registered");

        string memory fullName = string(abi.encodePacked(serviceType, "-", version));
        require($.serviceNameToAddress[fullName] == address(0), "Service name already exists");

        // Check interface support
        require(
            serviceAddress.supportsInterface(type(IERC165).interfaceId),
            "Service must support ERC165 interface detection"
        );
        require(
            serviceAddress.supportsInterface(SERVING_INTERFACE_ID),
            "Service must implement IServing interface"
        );

        // Register service (default not recommended)
        $.registeredServices[serviceAddress] = ServiceInfo({
            serviceAddress: serviceAddress,
            serviceContract: IServing(serviceAddress),
            serviceType: serviceType,
            version: version,
            fullName: fullName,
            description: description,
            isRecommended: false, // Default not recommended
            registeredAt: block.timestamp
        });

        $.serviceNameToAddress[fullName] = serviceAddress;
        $.serviceAddresses.add(serviceAddress);

        emit ServiceRegistered(serviceAddress, fullName);
    }

    function setRecommendedService(
        string memory serviceType,
        string memory version
    ) external onlyOwner {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        string memory fullName = string(abi.encodePacked(serviceType, "-", version));
        address serviceAddress = $.serviceNameToAddress[fullName];
        require(serviceAddress != address(0), "Service not found");

        // Clear all recommended flags for this service type
        _clearRecommendedServices($, serviceType);

        // Set new recommended service
        $.registeredServices[serviceAddress].isRecommended = true;

        emit RecommendedServiceUpdated(serviceType, version, serviceAddress);
    }

    function _clearRecommendedServices(LedgerManagerStorage storage $, string memory serviceType) internal {
        uint256 count = $.serviceAddresses.length();
        for (uint256 i = 0; i < count; i++) {
            address serviceAddr = $.serviceAddresses.at(i);
            ServiceInfo storage service = $.registeredServices[serviceAddr];

            if (keccak256(abi.encodePacked(service.serviceType)) == keccak256(abi.encodePacked(serviceType))) {
                service.isRecommended = false;
            }
        }
    }

    function getServiceInfo(address serviceAddress) external view returns (ServiceInfo memory) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        return $.registeredServices[serviceAddress];
    }

    function getServiceAddressByName(string memory serviceName) external view returns (address) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        return $.serviceNameToAddress[serviceName];
    }

    function getAllActiveServices() external view returns (ServiceInfo[] memory) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        uint256 count = $.serviceAddresses.length();
        ServiceInfo[] memory services = new ServiceInfo[](count);

        for (uint256 i = 0; i < count; i++) {
            address serviceAddress = $.serviceAddresses.at(i);
            services[i] = $.registeredServices[serviceAddress];
        }

        return services;
    }

    function getRecommendedService(string memory serviceType) 
        external view returns (string memory version, address serviceAddress) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        uint256 count = $.serviceAddresses.length();
        for (uint256 i = 0; i < count; i++) {
            address addr = $.serviceAddresses.at(i);
            ServiceInfo storage service = $.registeredServices[addr];

            if (keccak256(abi.encodePacked(service.serviceType)) == keccak256(abi.encodePacked(serviceType)) 
                && service.isRecommended) {
                return (service.version, addr);
            }
        }
        revert("No recommended service found for this type");
    }

    function getAllVersions(string memory serviceType) 
        external view returns (
            string[] memory versions,
            address[] memory addresses,
            bool[] memory isRecommendedFlags
        ) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        // First count matching services
        uint256 count = $.serviceAddresses.length();
        uint256 matchCount = 0;
        for (uint256 i = 0; i < count; i++) {
            address addr = $.serviceAddresses.at(i);
            ServiceInfo storage service = $.registeredServices[addr];
            if (keccak256(abi.encodePacked(service.serviceType)) == keccak256(abi.encodePacked(serviceType))) {
                matchCount++;
            }
        }

        // Allocate arrays
        versions = new string[](matchCount);
        addresses = new address[](matchCount);
        isRecommendedFlags = new bool[](matchCount);

        // Fill arrays
        uint256 index = 0;
        for (uint256 i = 0; i < count; i++) {
            address addr = $.serviceAddresses.at(i);
            ServiceInfo storage service = $.registeredServices[addr];
            if (keccak256(abi.encodePacked(service.serviceType)) == keccak256(abi.encodePacked(serviceType))) {
                versions[index] = service.version;
                addresses[index] = addr;
                isRecommendedFlags[index] = service.isRecommended;
                index++;
            }
        }
    }

    function isRecommendedVersion(string memory serviceType, string memory version)
        external view returns (bool) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        string memory fullName = string(abi.encodePacked(serviceType, "-", version));
        address serviceAddress = $.serviceNameToAddress[fullName];
        if (serviceAddress == address(0)) {
            return false;
        }
        return $.registeredServices[serviceAddress].isRecommended;
    }

    function _addProviderToService(LedgerManagerStorage storage $, address user, string memory serviceType, address provider) private {
        EnumerableSet.AddressSet storage providers = $.userServiceProviders[user][serviceType];
        providers.add(provider);
    }

    function spendFund(address user, uint amount) external onlyServing {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        // Use withLedgerLock to ensure single-user atomicity
        bytes32 key = _key(user);
        require(!$.ledgerMap._operationLocks[key], "Ledger locked for operation");
        $.ledgerMap._operationLocks[key] = true;

        Ledger storage ledger = _get($, user);
        require((ledger.totalBalance - ledger.availableBalance) >= amount, "Insufficient balance");
        ledger.totalBalance -= amount;

        $.ledgerMap._operationLocks[key] = false;
    }

    function _at(uint index) internal view returns (Ledger storage) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = $.ledgerMap._keys.at(index);
        return $.ledgerMap._values[key];
    }

    function _contains(LedgerManagerStorage storage $, bytes32 key) internal view returns (bool) {
        return $.ledgerMap._keys.contains(key);
    }

    function _length() internal view returns (uint) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        return $.ledgerMap._keys.length();
    }

    function _get(LedgerManagerStorage storage $, address user) internal view returns (Ledger storage) {
        bytes32 key = _key(user);
        if (!_contains($, key)) {
            revert LedgerNotExists(user);
        }
        return $.ledgerMap._values[key];
    }

    // Simplified _set without signer parameter
    function _set(
        LedgerManagerStorage storage $,
        bytes32 key,
        address user,
        uint balance,
        string memory additionalInfo
    ) internal {
        Ledger storage ledger = $.ledgerMap._values[key];
        ledger.availableBalance = balance;
        ledger.totalBalance = balance;
        ledger.user = user;
        ledger.additionalInfo = additionalInfo;
        $.ledgerMap._keys.add(key);
    }

    function _key(address user) internal pure returns (bytes32) {
        return keccak256(abi.encode(user));
    }

    receive() external payable {
        if (_isServiceContract(msg.sender)) {
            return;
        }

        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        bytes32 key = _key(tx.origin);

        // Check and set lock
        require(!$.ledgerMap._operationLocks[key], "Ledger locked for operation");
        $.ledgerMap._operationLocks[key] = true;

        // Deposit funds using internal function  
        _depositFundInternal(tx.origin, msg.value);

        // Release lock
        $.ledgerMap._operationLocks[key] = false;
    }

    function _isServiceContract(address sender) internal view returns (bool) {
        LedgerManagerStorage storage $ = _getLedgerManagerStorage();
        return $.registeredServices[sender].serviceAddress != address(0);
    }
}
